    private void pushActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pushActionPerformed
        String name =  nom.getText();
        String P_name = prenom.getText();
        int not = Integer.parseInt(note.getText());
        if (not<=20 && not>=0)
        {
            String query = " insert into etudiant(nom,prenom,moyenne)  values ('"+name+"','"+P_name+"','"+not+"')";
      
        
            try 
            {
             PreparedStatement preparedStmt = Con.connection.prepareStatement(query);
             preparedStmt.execute();
             confirm.setText("Congrats ! ");

            } catch (Exception e) 
            {
                System.out.println("err = "+e);
                e.printStackTrace();
            }
        }        // TODO add your handling code here:
    }//GEN-LAST:event_pushActionPerformed

