    private void seekActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_seekActionPerformed
        ResultSet rs;
        String query="SELECT * FROM etudiant where nom like'"+search.getText()+"%'";
        try { 
            Statement stm = Con.connection.createStatement();
            rs = stm.executeQuery(query);
            String x="";
            String s="";
            while (rs.next()) 
            {
                x=rs.getString(2);
          
                s=s + " " +x;
            }
            result.setText(s);

            
            
        } catch (SQLException ex) {
            Logger.getLogger(Formlaire.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_seekActionPerformed

